INSERT INTO genre (name, description)
VALUES ('Hành động', 'Phim có nhiều cảnh hành động, bạo lực, và đặc biệt là các cảnh hành động gay cấn và đánh nhau'),
       ('Phiêu lưu', 'Phim tập trung vào các cuộc phiêu lưu, thường đi kèm với một cuộc hành trình, khám phá hoặc chinh phục'),
       ('Hoạt hình', 'Phim được tạo ra bằng kỹ thuật hoạt hình, có thể là hoạt hình 2D, 3D, stop-motion, vv'),
       ('Phim hài', 'Phim có mục đích chính là làm cho khán giả cười thông qua các tình huống hài hước và lời nói hóm hỉnh'),
       ('Tâm lý', 'Phim tập trung vào việc phát triển nhân vật và các mâu thuẫn tinh thần, thường có nội dung nặng nề và sâu sắc'),
       ('Kinh dị', 'Phim tạo ra cảm giác kinh hoàng, sợ hãi thông qua việc sử dụng yếu tố ma quái, huyền bí hoặc bạo lực'),
       ('Khoa học viễn tưởng', 'Phim tập trung vào các yếu tố khoa học giả tưởng, thường liên quan đến vũ trụ, công nghệ tiên tiến và những trải nghiệm ngoài hành tinh'),
       ('Lãng mạn', 'Phim tập trung vào mối quan hệ tình cảm giữa các nhân vật chính, thường có yếu tố lãng mạn và tình yêu'),
       ('Hình sự', 'Phim tập trung vào các câu chuyện về tội phạm, công lý và cuộc chiến giữa cảnh sát và tội phạm'),
       ('Thần thoại', 'Phim chứa các yếu tố huyền bí, huyền diệu và thần thoại, thường liên quan đến thế giới thần thoại và phép thuật'),
       ('Chiến tranh', 'Phim tập trung vào các cuộc xung đột, chiến tranh và hậu quả của chúng'),
       ('Hồi hộp', 'Phim tạo ra cảm giác căng thẳng và hồi hộp thông qua việc sử dụng các yếu tố bất ngờ và kịch tính'),
       ('Bí ẩn', 'Phim tập trung vào việc giải quyết các bí ẩn và câu đố, thường có các yếu tố bí ẩn và ly kỳ'),
       ('Tội phạm', 'Tập trung vào các hoạt động tội phạm và cuộc đấu tranh của cảnh sát để bắt giữ tội phạm.'),
       ('Trinh thám', 'Liên quan đến việc giải quyết các vụ án hình sự thông qua các phương tiện pháp lý và tìm kiếm bằng chứng.'),
       ('Võ thuật', 'Tập trung vào các kỹ thuật võ thuật, võ sĩ và các cuộc đấu võ.'),
       ('Âm nhạc', 'Có các yếu tố âm nhạc và vũ đạo trong câu chuyện, thường là các bộ phim ca nhạc.'),
       ('Tiểu sử', 'Kể về câu chuyện thực sự của một người hoặc một nhóm người.'),
       ('Tài liệu', 'Tập trung vào việc ghi lại sự kiện, người nổi tiếng hoặc chủ đề đặc biệt với mục đích giáo dục hoặc giải trí.'),
       ('Thể thao', 'Tập trung vào các sự kiện thể thao và các câu chuyện xoay quanh các vận động viên, đội tuyển hoặc các sự kiện thể thao lớn.'),
       ('Cổ trang', 'Diễn ra trong một thời kỳ lịch sử hoặc trong một nền văn hóa cổ đại.'),
       ('Gia đình', 'Dành cho công chúng mọi lứa tuổi, thường có nội dung giáo dục và giải trí cho cả gia đình.'),
       ('Lịch sử', 'Kể về các sự kiện lịch sử quan trọng hoặc các nhân vật lịch sử nổi tiếng.'),
       ('Chính kịch', 'Có nội dung nặng nề và sâu sắc, thường khám phá về con người và xã hội.');
