INSERT INTO movie (name, description, directors, actors, duration, released_date, country, language, localizations, format, rating, poster_url, trailer_url, state)
VALUES ('Lật mặt 7: Một điều ước', 'Một câu chuyện lần đầu được kể bằng tất cả tình yêu, và từ tất cả những hồi ức xao xuyến nhất của đấng sinh thành.', 'Lý Hải', 'Thanh Hiền, Trương Minh Cường, Đinh Y Nhung, Quách Ngọc Tuyên, Trâm Anh, Trần Kim Hải', 138, '2024-04-26', '', '', '', '2D', 'K', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fposter-lat-mat-7.jpg?alt=media&token=2949e81e-e5d5-4851-8440-53eac7c98780', '', 'showing'),
       ('Quật mộ trùng ma', 'Dưới đôi tay của Bậc thầy kinh dị Jang Jae Hyun - cha đẻ của "Svaha: Ngón Tay Thứ Sáu" và "Mục Sư", phim điện ảnh nổi bật nhất năm 2024 của Hàn Quốc hứa hẹn mang đến trải nghiệm mãn nhãn trên màn ảnh rộng tại Việt Nam vào tháng 3 này!', 'Jang Jae-hyun', 'Md Rustam Ansari, Jeon Jin-gi, Lee Do-hyun, Yoo Hae-jin, Kim Go-Eun', 133, '2024-03-15', '', '', '', '2D', 'T16', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fquatmotrungma.jpg?alt=media&token=db8c0173-7e07-4dbd-b700-4a62240fb8cc', '', 'showing'),
       ('Gozilla x Kong', 'Kong và Godzilla - hai sinh vật vĩ đại huyền thoại, hai kẻ thủ truyền kiếp sẽ cùng bắt tay thực thi một sứ mệnh chung mang tính sống còn để bảo vệ nhân loại, và trận chiến gắn kết chúng với loài người mãi mãi sẽ bắt đầu.', 'Adam Wingard', 'Dan Stevens, Rebecca Hall, Brian Tyree Henry, Kaylee Hottle, Fala Chen', 115, '2024-03-29', '', '', '', '2D', 'K', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fgodzilla_va_kong.jpg?alt=media&token=b27ea862-62f4-49f7-a69e-85fff5071e03', '', 'showing'),
       ('Kung Fu Panda 4', 'Sau khi Po được chọn trở thành Thủ lĩnh tinh thần của Thung lũng Bình Yên, Po cần tìm và huấn luyện một Chiến binh Rồng mới, trong khi đó một mụ phù thủy độc ác lên kế hoạch triệu hồi lại tất cả những kẻ phản diện mà Po đã đánh bại về cõi linh hồn.', 'Mike Mitchell', 'Lucy Liu, David Cross, Seth Rogen, Jackie Chan, Angelina Jolie', 94, '2024-08-03', '', '', '', '2D', 'P', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fkungfupanda.jpg?alt=media&token=f2cc5a11-10e7-4ab9-b788-2fc6220fc549', '', 'coming_soon'),
       ('Quỷ thuật', 'Sylvie phải gánh chịu lời nguyền do tội ác man rợ mà cha chồng cô đã gây ra. Ngày cô mang thai đứa con của mình cũng chính là ngày ác quỷ trở về từ cõi chết.', 'Visal Sem', 'Kirty Marie Day, Brendan Gallagher, Robert Uwe Mueller, Khemma Maria Metayer', 106, '2024-03-29', '', '', '', '2D', 'T16', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fquythuat.jpg?alt=media&token=0bf96092-4d56-43f8-b602-59e53692ef84', '', 'showing'),
       ('Sáng đèn', 'Câu chuyện trong Sáng Đèn có bối cảnh trải dài từ thập niên 1990 đến những năm 2000. Phim là hành trình nhiều thăng hoa nhưng cũng đầy biến cố của đoàn cải lương Viễn Phương với những người nghệ sĩ tài hoa. Từ một đoàn cải lương chính thống, để duy trì họ phải buộc chuyển mình, trở thành một đoàn tạp kỹ. Mặc dù phải vật lộn với hoàn cảnh khó khăn của đoàn hát nhưng tình yêu nghề, tình yêu đôi lứa vẫn luôn bỏng cháy. Số phận đoàn sẽ đi về đâu là một dấu hỏi lớn? Và tình yêu với cải lương có giúp họ vượt qua nghịch cảnh để tiếp tục đời “con tằm đến thác vẫn còn vương tơ”.', 'Hoàng Tuấn Cường', 'Hữu Châu, Chí Tâm, Bạch Công Khanh, Lê Phương, Cao Minh Đạt', 128, '2024-03-22', '', '', '', '2D', 'K', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fsangden.jpg?alt=media&token=699f434f-f421-4c3d-baf6-bfdbe11946f5', '', 'showing'),
       ('Muôn vị nhân gian', 'Phim điện ảnh Muôn Vị Nhân Gian lấy bối cảnh tại một vùng quê nước Pháp năm 1885, xoay quanh mối tình lãng mạn giữa nữ đầu bếp Eugénie (Juliette Binoche) và Dodin (Benoît Magimel) - một chuyên gia ẩm thực với đam mê sáng tạo nên những món ăn trọn vẹn từ hương vị đến hình thức. Khởi nguồn từ tình yêu ẩm thực để cùng nhau tạo ra nhiều món ăn tinh tế và lộng lẫy trong suốt 20 năm, thu hút các thực khách từ khắp nơi trên thế giới đến thưởng thức, tình yêu đôi lứa dần nảy nở giữa Eugénie và Dodin. Tuy nhiên, Eugénie lại là một người phụ nữ yêu tự do và chưa sẵn sàng kết hôn với Dodin. Để chinh phục được trái tim của nàng, Dodin đã quyết định vào bếp, lần đầu tiên nấu ăn cho người mình yêu.', 'Tran Anh Hung', 'Juliette Binoche, Benoît Magimel, Bonnie Chagneau-Ravoire', 135, '2024-03-22', '', '', '', '2D', 'T13', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fmuon_vi_nhan_gian.jpg?alt=media&token=7f5fd66f-2587-472b-8999-6b15adcb736d', '', 'showing'),
       ('Người chết trở về', 'Bị dồn vào đường cùng, Lee Man Jae (Jo Jin Woong) phải bán cả tên của mình để tồn tại. Khi đứa con đầu lòng chuẩn bị chào đời, Man Jae quyết định thực hiện một phi vụ lớn cuối cùng, nhưng lại bị vướng vào vụ tham ô trị giá 100 tỉ won. Anh sống như một kẻ đã chết, cho đến một ngày kia, một chính trị gia họ Shim bí ẩn (Kim Hee Ae) đề nghị sẽ giúp anh lấy lại danh tính và cuộc đời mình. Lee Man Jae bắt đầu hành trình "trở về từ cõi chết", đồng thời lật mở những bí mật nhơ nhuốc của xã hội.', 'Hah Jun-won', 'Cho Jin-woong, Kim Hee-ae, Lee Soo-kyung, Choi Soo-young, Choi Jae-woong', 109, '2024-03-22', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fnguoichettrove.jpg?alt=media&token=8d0e6b62-8b5b-4f2a-a315-71c94e257d12', '', 'showing'),
       ('Tình yêu và cám dỗ', 'Ahmed vừa tròn 18 tuổi, là người Pháp gốc Algeria. Cậu lớn lên ở ngoại ô Paris. Ahmed gặp Farah, một cô nàng đến từ Tunisia tràn đầy năng lượng vừa đến học ở Paris. Trong khi học về nền văn học Ả Rập gợi cảm và có phần khiêu dâm ở trường, Ahmed đã nảy sinh tình cảm với Farah và luôn bị ham muốn bao quanh. Thế nhưng, Ahmed luôn tìm mọi cách kìm lại!', 'Leyla Bouzid', 'Sami Outalbali, Zbeida Belhajamor, Diong-Kéba Tacu, Aurélia Petit, Mahia Zrouki', 102, '2024-03-30', 'Pháp', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Ftinhyeuvacamdo.jpg?alt=media&token=b6a0fcde-fba3-474f-bba0-66ec6ce96da9', '', 'showing'),
       ('Dune 2: Hành tinh cát', 'Dune. 2: Hành Tinh Cát sẽ tiếp tục khám phá hành trình đậm chất thần thoại của Paul Atreides khi anh đồng hành cùng Chani và những người Fremen trên chặng đường trả thù những kẻ đã hủy hoại gia đình mình. Đối mặt với những lựa chọn giữa tình yêu của cuộc đời mình và số phận của vũ trụ, Paul phải ngăn chặn viễn cảnh tương lai tồi tệ chỉ mình anh nhìn thấy.', 'Denis Villeneuve', 'Timothee Chalamet, Zendaya, Rebecca Ferguson, Javier Bardem, Florence Pugh', 166, '2024-01-03', '', '', '', '2D', 'T16', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fdune2.jpg?alt=media&token=75a722e4-50fb-4000-be5f-a96ce1b076d5', '', 'showing'),
       ('Quý cô thừa kế 2', '“Có mấy ông lắm tiền nhiều của, vợ bé con riêng, gia đình tan nát"... Dần dần câu chuyện thật của giới thượng lưu ngàn tỷ được hé mở. Từ những buổi tiệc thác loạn, đến gia đình đại gia nhưng lộ ra là những bí mật ''dơ bẩn'' về một gia đình quyền quý… Quý Cô Thừa Kế 2 hứa hẹn sẽ tháo bỏ lớp mặt nạ hào nhoáng của thế giới thượng lưu để lộ ra một "âm mưu vô cùng đen tối"...', 'Hoàng Duy', 'Trang Nhung, Huy Khánh, Lâm Vỹ Dạ, Hứa Minh Đạt, Quyên Qui. Otis. Thanh Trâm.', 110, '2024-08-03', '', '', '', '2D', 'T16', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fquy_co_thua_ke_2.jpg?alt=media&token=5448fd85-a005-4f4f-8002-119baf3dfea7', '', 'showing'),
       ('Mai', 'Phim Mai của Trấn Thành khởi chiếu vào mùng 1 Tết 2024 - 10.2.2024. Mai xoay quanh câu chuyện về tình chị em “dở khóc dở cười” của Mai (Phương Anh Đào) và Dương (Tuấn Trần), cùng sự giao thoa bí ẩn giữa câu chuyện quá khứ - tương lai liên tục được nhắc đến.', 'Trấn Thành', 'Phương Anh Đào,Tuấn Trần,Trấn Thành,Hồng Đào,Huỳnh Uyển Ân', 131, '2024-10-02', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2FMai.jpg?alt=media&token=8b376d54-bb6a-4606-94ec-168fb4e13cf0', '', 'showing'),
       ('Móng vuốt', 'Móng Vuốt xoay quanh nhóm bạn chơi thân với nhau từ thời trung học. Nhằm kỷ niệm cột mốc 10 năm gắn bó, cả hội quyết định rời xa phố thị, tìm đến khu rừng vùng ngoại ô “đổi gió”. Tiếc rằng, chuyến đi dã ngoại tưởng chừng đáng nhớ ấy bỗng chốc trở thành cơn ác mộng kinh hoàng, khi họ bắt gặp ánh mắt của một sinh vật nguy hiểm đang rình rập trong bóng tối.', 'Lê Thanh Sơn', 'Tuấn Trần, Nguyễn Lâm Thảo Tâm, Rocker Nguyễn, Gi A Nguyễn, Nguyễn Quốc Khánh...', 110, '2024-05-04', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fmong-vuot.jpg?alt=media&token=e4288d59-fb91-4909-a40e-8ade94c2bef4', '', 'showing'),
       ('Quỷ cái', 'Một bộ phim kinh dị nặng đô của Thái Lan dự tính ra mặt rạp phim Việt trong tháng 4', 'Artistaya Arriyawongsa, Theerakhaha Arriyawongsa.', 'Anchalee Saisoontorn, Jay Jatuporn Dangurai, Latkamon Pinrojkirati...', 120, '2024-05-04', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fquy_cai.jpg?alt=media&token=a7f5c09d-ce37-4b0c-b8dc-b4f5fa5cd23e', '', 'showing'),
       ('B4S - Trước giờ yêu', 'B4S - Trước Giờ Yêu sẽ bắt đầu bằng một đêm “nhớ đời” của hai nhóm bạn thân tại quán bar. Và sau đó, khán giả sẽ được theo dõi câu chuyện tình yêu của từng người trong “đêm định mệnh” ấy.', 'Tùng Leo, Michael Thái, Huỳnh Anh Duy.', 'Jun Vũ, Tôn Kinh Lâm, Khánh Vân, Vinh Râu...', 108, '2024-04-19', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2FB4S.jpg?alt=media&token=0aeb4cf5-7cd4-4c6b-ac1e-ffdaf19a6f1f', '', 'showing'),
       ('Ngày tàn của đế quốc', 'Bộ phim Ngày Tàn Của Đế Quốc của đạo diễn Alex Garland. Trong một tương lai gần, một nhóm các phóng viên chiến trường quả cảm phải đấu tranh với thời gian và bom đạn nguy hiểm để đến kịp Nhà Trắng giữa bối cảnh nội chiến Hoa Kỳ đang tiến đến cao trào.', 'Alex Garland', 'Jaclyn White, Robert Tinsley, Lauren Marie Gordon, Ryan Austin Bryant, Tywaun Tornes', 96, '2024-12-04', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fngay-tan-cua-de-quoc_1.jpg?alt=media&token=1e1bef0b-e597-4731-b7cd-691d3b6b855c', '', 'showing'),
       ('Hào quang đẫm máu', 'Chuyện phim xoay quanh Lee Soo-yeon (Park Ji-yeon) - một nữ minh tinh màn bạc đang ở đỉnh cao sự nghiệp nhưng bất chợt tuột dốc không phanh vì tai nạn chết người do chính cô gây ra trong lúc say rượu. Sau thời gian dài “ở ẩn” để trả giá cho sai lầm, Lee Soo-yeon bắt đầu nuôi mộng trở lại với khán giả nhưng bê bối từ quá khứ của cô luôn bị người đời đay nghiến, châm chọc. Lòng đố kỵ và khát khao danh vọng trỗi dậy trong Soo-yeon khi hằng ngày chứng kiến sự thành công của “ngôi sao mới lên” Song Ga-young (Kim Nu-ri) - hậu bối cùng công ty quản lý được sắp xếp sống chung với Soo-yeon trong căn hộ sang trọng. Sau một cuộc tranh cãi gay gắt với Ga-young, Soo-yeon đã tìm đến rượu để “an thần”, nhưng khi thức dậy cô đã phát hiện Ga-young chết trong căn hộ đầy bí ẩn. Liệu án mạng này sẽ ảnh hưởng như thế nào đến con đường lấy lại hào quang của Soo-yeon khi chính cô là nghi phạm duy nhất?', 'Yoo Young-seon', 'Park Ji-yeon, Song Ji-eun, Kim Nu-ri, Lee Ji-hun, Kim He-sang', 113, '2024-12-04', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fhao-quang-dam-mau-poster.jpg?alt=media&token=3d947679-977f-48bf-8b9e-c96bf2012f4c', '', 'showing'),
       ('Thanh Xuân 18x2: Lữ trình hướng về em', 'Ký ức tình đầu ùa về khi Jimmy nhận được tấm bưu thiếp từ Ami. Cậu quyết định một mình bước lên chuyến tàu đến Nhật Bản gặp lại người con gái cậu đã bỏ lỡ 18 năm trước. Mối tình day dứt thời thanh xuân, liệu sẽ có một kết cục nào tốt đẹp khi đoàn tụ?', 'Michihito Fujii', 'Greg Hsu, Kaya Kiyohara, Zhang Xiaoquan, Shunsuke Michieda, Haru Kuroki', 106, '2024-12-04', '', '', '', '2D', 'T13', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fthanhxuan18x2.jpg?alt=media&token=64c5446b-8adc-410c-8bd7-adaed5193891', '', 'showing'),
       ('Biệt đội săn ma: Kỷ nguyên băng giá', 'Sau các sự kiện của Ghostbusters: Afterlife, gia đình Spengler đang tìm kiếm cuộc sống mới ở Thành phố New York. Nhóm săn ma bao gồm Ray, Winston và Podcast sử dụng công nghệ mới để chống lại các mối đe dọa chết người cổ xưa đang ẩn náu trong các vật dụng hàng ngày. Thế nhưng, họ sẽ phải đối đầu với một thế lực đen tối hùng mạnh mới.', 'Gil Kenan', 'William Atherton, Emily Alyn Lind, James Acaster, Annie Potts, Ernie Hudson', 120, '2024-12-04', '', '', '', '2D', 'T13', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fbiet_doi_san_ma.jpg?alt=media&token=3273cbce-05d8-4999-8b8d-89e633d58009', '', 'showing'),
       ('Yêu cuồng loạn', 'Cuộc tình mới chớm nở giữa nữ quản lý phòng tập Lou (Kristen Stewart) và vận động viên thể hình đầy tham vọng Jackie (Katy O’Brian) khiến cả hai bị lún sâu vào chuỗi những rắc rối đẫm máu liên quan đến gia đình tội phạm khét tiếng của Lou.', 'Rose Glass', 'Crystal Mayes, J Diego Gonçalves, Kim S. Monti, Eldon Jones, Matthew Blood-Smyth', 104, '2024-04-19', '', '', '', '2D', 'T18', 'https://firebasestorage.googleapis.com/v0/b/cinemix-7dffb.appspot.com/o/images%2Fmovies%2Fyeu_cuong_loan.jpg?alt=media&token=574ab0ea-46fa-4dfa-b5aa-a38398a6c78e', '', 'showing');

